import { Injectable } from "@angular/core";
import { HttpClient } from '@angular/common/http';

@Injectable({
    providedIn: 'root'
})
export class HomepageService {
    constructor(private httpClient: HttpClient) {

    }

    getHomepageProducts() {
        this.httpClient.get('http://localhost:8080/homepage/products').subscribe(
            {
                next(value) {
                    return value;
                },
                error(err) {
                    console.error(err);
                },
            }
        );
    }
}
